class State {
    constructor(source) { this._source = source; }
    execute() { }
}
module.exports.State = State;

class InProgressState extends State {
    constructor(request) { super(request); }
}
module.exports.InProgressState = InProgressState;

class FinishedState extends State {
    constructor(request, response) { super(request); this._response = response; }
}
module.exports.FinishedState = FinishedState;

class ErrorState extends State {
    constructor(error, request) { super(request); this._error = error; }
}
module.exports.ErrorState = ErrorState;
