'use strict';
const AbstractEncoder = require('./AbstractEncoder');
const V2UriLiteralEncoder = require('../../utils/V2UriLiteralEncoder');

class ODataV2Encoder extends AbstractEncoder {

    constructor() {
        super();
        this._uriLiteralEncoder = new V2UriLiteralEncoder();
    }

    /**
     * @inheritDoc
     * @return {string}
     */
    encodeUriLiteral(value, type) {
        return this._uriLiteralEncoder.encode(value, type);
    }

    /**
     * @inheritDoc
     * @returns {string}
     */
    encodeQueryOption(name, value) {
        return value;
    }

}

module.exports = ODataV2Encoder;
