'use strict';
const AbstractEncoder = require('./AbstractEncoder');
const V4UriLiteralEncoder = require('../../utils/V4UriLiteralEncoder');

class ODataV4Encoder extends AbstractEncoder {

    constructor() {
        super();
        this._uriLiteralEncoder = new V4UriLiteralEncoder();
    }

    /**
     * @inheritDoc
     * @return {string}
     */
    encodeUriLiteral(value, type) {
        return this._uriLiteralEncoder.encode(value, type);
    }

    /**
     * @inheritDoc
     * @returns {string}
     */
    encodeQueryOption(name, value) {
        return value;
    }

}

module.exports = ODataV4Encoder;
