'use strict';

const AbstractUriFormatter = require('./AbstractUriFormatter');

class StringFormatter extends AbstractUriFormatter {

    /**
     * Formats the Uri as a string
     * @param {string[]} pathSegments
     * @param {Map} queryOptions
     * @return {string} the uri formatted as string
     */
    format(pathSegments, queryOptions) {
        let uri = pathSegments.join('/');
        if (queryOptions.size > 0) {
            uri += '?' + Array.from(queryOptions).map(pair => pair.join('=')).join('&');
        }
        return uri;
    }
}

module.exports = StringFormatter;
