'use strict';

class AbstractLiteralDecoder {

    /**
     * Decodes a literal to a built in JavaScript-type
     * @abstract
     * @param {boolean|number|string|null} literal The literal that should be decoded
     * @param {EdmPrimitiveType} type Type of the literal. Must be a member of EdmPrimitiveTypeKind.
     * @return {Buffer|boolean|number|string|null}
     */
    // eslint-disable-next-line no-unused-vars
    decode(literal, type) {
        throw new Error('.decode must be implemented by subclass');
    }
}

module.exports = AbstractLiteralDecoder;
