'use strict';

class AbstractValueEncoder {

    /**
     * Encodes a javascript-value to a literal
     * @abstract
     * @param {Buffer|boolean|number|string|null} value The literal that should be decoded
     * @param {EdmPrimitiveType} type Type of the literal. Must be a member of EdmPrimitiveTypeKind.
     * @return {boolean|number|string|null}
     */
    // eslint-disable-next-line no-unused-vars
    encode(value, type) {
        throw new Error('.encode must be implemented by subclass');
    }
}

module.exports = AbstractValueEncoder;
